const { GPT } = require("d-scrape").ai

const gpt = new GPT()

async function gpt4(prompt) {
  const messages = [
    { role: "assistant", content: "Dann-Legacy adalah bot WhatsApp yang terbuat dari Nodejs, Python. Untuk membantu anda dalam mengerjakan dalam hal apapun." },
    { role: "user", content: prompt }
  ];
  let res = await gpt.fetchData(messages)
  return  res
}

gpt4("halo")